    def extract_progress_values(text):
        # 正则表达式匹配 "pointProgressMax" 和 "pointProgress"
        pattern = r'"pointProgressMax":(\d+),"pointProgress":(\d+)'
        matches = [match for match in re.finditer(pattern, text)]
        # 使用 finditer 遍历所有匹配项,
