def random_chinese_GBK2312(n=1):
    strGB = ""
    while n > 0:
        head = random.randint(0xb0, 0xf7)
        body = random.randint(0xa1, 0xfe)
        val = f'{head:x} {body:x}'
        n -= 1
        strGB += bytes.fromhex(val).decode('gb2312')
    return strGB


